1) gen dxy :
const math::XYZPoint & vtx = genParticle.vertex();
float phi = genParticle.momentum().phi();
float dxyGen = ( - ( vtx.x() - beamspot->x0())_sin(phi) + (vtx.y() - beamspot->y0())_cos(phi));
